/**对比 */
<template>
  <div id="contrast">
    <el-row class="contrastTop">
      <span class="contrastForm">
        <el-form :inline="true"
                 ref="searchForm"
                 :model="searchForm">
          <el-form-item label="车辆品牌:">
            <el-input v-model="searchForm.clpp"
                      placeholder=""></el-input>
          </el-form-item>
          <el-form-item label="零部件名称:">
            <el-input v-model="searchForm.lbjmc"
                      placeholder=""></el-input>
          </el-form-item>
        </el-form>
      </span>

      <span class="contrastButton">
        <el-button type="primary"> 检索</el-button>
        <el-button plain
                   @click="reset">重置</el-button>
      </span>

    </el-row>
    <el-row>
      <div class="tableTitle">
        <span class="title_t">车辆列表</span>
        <span class="title_b">
          <el-button type=""
                     @click="showContrast">展开已选对比</el-button>
        </span>
      </div>
      <componentsTable :tableData="tableData"
                       :tableSelection="showSelection"
                       :operation='operation'
                       :tableIndex="tableIndex"
                       :tableHead="tableHead"
                       :operationArr="operationArr"
                       @choseselection="selctionChose"
                       @buttonclick="clickButton"></componentsTable>
    </el-row>
    <el-drawer title="我是标题"
               :visible.sync="drawer"
               :with-header="false">
      <div>
        <span>
          已选对比
        </span>
        <span>
          收起<i class="el-icon-arrow-right"></i>
        </span>
      </div>
      <div>
        <componentsTable :tableSelection="showSelection"
                         :tableData="tableData"
                         :operation='false'
                         :tableIndex="tableIndex"
                         :tableHead="tableHead"
                         :operationArr="operationArr"
                         @choseselection="selctionChose"
                         @buttonclick="clickButton"></componentsTable>
      </div>
      <div>
        <el-button @click="detailShow = true">开始对比</el-button>
      </div>
    </el-drawer>
    <el-dialog title="对比详情"
               :visible.sync="detailShow">
      <componentsTable :tableSelection="false"
                       :operation='false'
                       :tableIndex="false"
                       :tableData="detailsData"
                       :tableHead="detailsHead"></componentsTable>
    </el-dialog>
  </div>
</template>
<script>
import componentsTable from '@/components/table/componentsTable'
export default {
  components: { componentsTable },
  name: 'contrast',
  data () {
    return {
      tableData: [],
      showSelection: true,
      operation: true,
      tableIndex: true,
      tableHead: [
        { label: '车辆品牌' },
        { label: '车辆名称' },
        { label: '车辆类型' },
        { label: '燃料类型' },
        { label: '车型级别' },
        { label: '总成名称' },
        { label: '零部件名称' },
        { label: '零部件供应商', width: '120px' },
        { label: '零部件重量' },
        { label: '零部件用材' },
      ],
      operationArr: [
        { icon: "el-icon-edit-outline", title: '加入对比', name: '加入对比' },
      ],
      searchForm: {
        clpp: '',
        lbjmc: ''
      },
      drawer: false,
      detailShow: false,
      detailsHead: [
        { label: '零部件【车型名称】', width: '160px' },
      ],
      detailsData: []
    }
  },
  methods: {
    reset () {
      this.searchForm.clpp = ''
      this.searchForm.lbjmc = ''
    },
    showContrast () {
      this.drawer = true
    }
  }
}
</script>
<style lang="scss" scoped>
#contrast /deep/ .el-drawer__container {
  top: 60px;
  bottom: 0;
  height: calc(100% - 60px);
}
#contrast {
  .contrastTop {
    width: 96%;
    height: 80px;
    margin: 20px auto;
    background-color: #ffffff;
    .contrastForm {
      display: block;
      float: left;
      margin-left: 24px;
      margin-top: 20px;
    }
    .contrastButton {
      display: block;
      float: right;
      margin-right: 24px;
      margin-top: 20px;
    }
  }
  .tableTitle {
    width: 96%;
    height: 50px;
    background: #ffffff;
    margin: 0px auto 4px;
    .title_t {
      display: block;
      float: left;
      line-height: 40px;
      margin-left: 8px;
    }
    .title_b {
      display: block;
      float: right;
      margin-right: 6px;
    }
  }
}
</style>
